+std::string Status::ModeToString(const Mode & mode)
+{
+  switch (mode) {
+    case Mode::JOINT:
+      return "Joint";
+    case Mode::CARTBASE:
+      return "Cartesian (base)";
+    case Mode::CARTTOOL:
+      return "Cartesian (tool)";
+    case Mode::PLATFORM:
+      return "Platform";
+    case Mode::FSM:
+      return "FSM";
+    case Mode::UNKNOWN:
+      return "Unknown";
+  }
+
+  return std::string();
+}
