+std::string Variables::GetMatchString(const std::smatch & smatch)
+{
+  std::string matchStr = smatch.str();
+  if (matchStr.length() > 0 && matchStr.back() == ',') {
+    matchStr.pop_back();
+  }
+  return matchStr;
