+void Rebel::SetDigitalOut(const int & output, const bool & is_on)
+{
+  std::ostringstream cmd;
+  cmd << CriKeywords::COMMAND_DOUT << " " << output << " " << (is_on ? "true" : "false");
+  Command(cmd.str());
